INSERT INTO `wp_3_posts` VALUES("1660", "3", "2014-03-29 10:03:40", "2014-03-29 10:03:40", "[vc_row el_class=\"no-padding sub-slider\"][vc_column width=\"1/1\"][vc_single_image image=\"1636\" img_link_target=\"_self\" img_size=\"full\"][/vc_column][/vc_row][vc_row el_class=\"menu-content\"][vc_column width=\"1/1\"][vc_tabs][vc_tab title=\"Brunch Menu\" tab_id=\"1395899423-1-28\"][vc_column_text]\r\n<h2 style=\"text-align: center; margin-bottom: 7px;\">FROM THE OVEN</h2>\r\n<p style=\"text-align: center; font-size: 12px;\"><em>traditional pidettes with your choice of</em></p>\r\n<p style=\"text-align: center;\"><strong>GROUND SEASONED LAMB</strong> 5</p>\r\n<p style=\"text-align: center;\"><strong>KASSERI CHEESE</strong> 4</p>\r\n<p style=\"text-align: center;\"><strong>SOUJOUK &amp; KASSERI</strong> 5</p>\r\n<p style=\"text-align: center;\"><strong>SPINACH &amp; PINE NUTS</strong> 5</p>\r\n<p style=\"text-align: center;\"><strong>WILD MUSHROOM, ROASTED GARLIC &amp; CHECHIL CHEESE FLATBREAD</strong> 8</p>\r\n\r\n<h2 style=\"text-align: center;\"></h2>\r\n<h2 style=\"text-align: center;\">SALADS</h2>\r\n<p style=\"text-align: center; line-height: 1.5em;\"><strong>PERA-STYLE TOMATO AND ONION</strong> cubed tomatoes, finely chopped onions and parsley, peeled walnuts in pomegranate - lemon dressing 12</p>\r\n<p style=\"text-align: center; line-height: 1.5em;\"><strong>GRAPEFRUIT AND AVOCADO SALAD</strong> multi color grape tomatoes, mache, onions, red radish and toasted almonds  13</p>\r\n<p style=\"text-align: center; line-height: 1.5em;\"><strong>MAROUL SALAD</strong> shredded romaine hearts, dill, scallion, feta cheese, lemon and extra-virgin olive oil  12</p>\r\n<p style=\"text-align: center; line-height: 1.5em;\"><strong>BLACK KALE CAESAR</strong> red radish, red bell peppers, candied walnuts, lemon-yogurt dressing  12</p>\r\n<p style=\"text-align: center; line-height: 1.5em;\"><strong>MEDITERRANEAN PEASANT SALAD</strong> cucumbers, cherry, tomatoes, parsley, onion, red wine vinaigrette  13</p>\r\n<p style=\"text-align: center; line-height: 1.5em;\">add crumbled sheep\'s milk feta cheese to any of the above salads  13</p>\r\n[/vc_column_text][/vc_tab][vc_tab title=\"Dinner Menu\" tab_id=\"1395899620491-1-7\"][vc_column_text]\r\n<p style=\"text-align: center;\"><em>5:00PM - 9:00PM</em></p>\r\n\r\n<h2 style=\"text-align: center;\">Appetizers</h2>\r\n<h5 style=\"text-align: center;\"><em>(please select one)</em></h5>\r\n<p style=\"text-align: center;\"><strong>AVGOLEMONO SOUP</strong> greek-style chicken, lemon &amp; egg soup</p>\r\n<p style=\"text-align: center;\"><strong>MEDITERRANEAN ANGEL EGGS</strong> deviled eggs with spicy hummus</p>\r\n<p style=\"text-align: center;\"><strong>TURKISH ZUCCHINI PANCAKES</strong> smoked salmon and garlic labne</p>\r\n<p style=\"text-align: center;\"><strong>PERA EASTER SALAD</strong> wild arugula, tomatoes, onions, fried chickpeas, purpled egg</p>\r\n\r\n<h2 style=\"text-align: center;\">Main Courses</h2>\r\n<h5 style=\"text-align: center;\"><em>(please select one)</em></h5>\r\n<p style=\"text-align: center;\"><strong>MUSTARD HERB CRUSTED HAM</strong> chili glazed carrots, dirty potatoes</p>\r\n<p style=\"text-align: center;\"><strong>INSIDE OUT LAMB PERSILLADE</strong> couscous with dried apricots and pine nuts</p>\r\n<p style=\"text-align: center;\"><strong>PAN SEARED MEDITERRANEAN DORADE</strong> mixed vegetable and quinoa tabouleh</p>\r\n<p style=\"text-align: center;\"><strong>SPRING VEGETABLE BUCATINI</strong> hollow tube pasta with sundried tomatoes, asparagus and garlic butter sauce</p>\r\n\r\n<h2 style=\"text-align: center;\">Dessert</h2>\r\n<h5 style=\"text-align: center;\"><em>(please select one)</em></h5>\r\n<p style=\"text-align: center;\"><strong>GOATS MILK CHEESE CAKE</strong> roasted fig syrup</p>\r\n<p style=\"text-align: center;\"><strong>PORTOKALOPITA</strong> warm orange cake</p>\r\n<p style=\"text-align: center;\"><strong>CHOCOLATE CARDAMOM MOUSSE</strong> sweet turkish cream cheese</p>\r\n<p style=\"text-align: center;\"><strong>$29</strong></p>\r\n\r\n<h5 style=\"text-align: center;\"><em>(not including tax and gratuity)</em></h5>\r\n[/vc_column_text][/vc_tab][/vc_tabs][/vc_column][/vc_row]", "Happy Easter", "", "publish", "closed", "closed", "", "happy-easter-2014", "", "", "2014-03-30 14:58:31", "2014-03-30 14:58:31", "", "0", "http://midtown.pera.wpengine.com/?post_type=event&#038;p=1660", "0", "event", "", "0");
