<?php 
function cgm_install_capabilities(){
    $check_install = get_option('cgm_version_check'); 	
	if($check_install < CGM_VERSION){
		cgm_install_capabilities2();

		$caps = array();
		$caps[] = 'cgm_create_gallery';
		$caps[] = 'cgm_upload_images';
		$caps[] = 'cgm_select_images';
		$caps[] = 'cgm_insert_gallery';
		$caps[] = 'cgm_template_save';
		$caps[] = 'cgm_template_customize';
		$caps[] = 'cgm_options';
		$caps[] = 'cgm_license';
	
		$WP_Roles = new WP_Roles();	
		foreach($caps as $cap){
			$WP_Roles->add_cap( 'administrator', $cap );
		}
		
		update_option('cgm_version_check',CGM_VERSION);
	}
}	
	
	
